import React from "react"; // usestate is a hook to follow the state of the variable
import { Text, StyleSheet, View, Button } from "react-native";

const CounterScreen = ({navigation}) => {  // stands for props.navigation

  return (
      <View>
          <Button title="Add a colour" />
          <View style={{ height: 100, width:100, backgroundColor: 'rgb(0,255,0)'}} />

      </View>
  );
};

const ramdomRgb = () => {
    const red = Math.random()*256;
    const gren = Math.random()*256;
    const blue = Math.random()*256;
    return 'rgb(${red}, ${green}, ${blue})'
}
const styles = StyleSheet.create({
  text: {
    fontSize: 30
  }
});

export default CounterScreen;
