import React, {useState} from "react"; // usestate is a hook to follow the state of the variable
import { Text, StyleSheet, View, Button, FlatList } from "react-native";

const CounterScreen = ({navigation}) => {  // stands for props.navigation
    const [colors, setColors] = useState([]);
    console.log(colors);

  return (
      <View>
          <Button 
          title="Add a colour" 
              onPress={() => {
          setColors([...colors, randomRgb()])
        }}
          />

<FlatList 
    keyExtractor={(item) => item}
    data={colors}
    renderItem={({ item}) => {
    return <View style={{ height: 100, width:100, backgroundColor: randomRgb() }} />
    }}
/>

      </View>
  );
};

const randomRgb = () => {
    const red = Math.floor(Math.random()*256);
    const green = Math.floor(Math.random()*256);
    const blue = Math.floor(Math.random()*256);
    return `rgb(${red}, ${green}, ${blue})`;
};

const styles = StyleSheet.create({
  text: {
    fontSize: 30
  }
});

export default CounterScreen;
