import React, {useState} from "react"; // usestate is a hook to follow the state of the variable
import { Text, StyleSheet, View, Button } from "react-native";
import ColorCounter from '../Components/ColorCounter';


const SquareScreen = ({navigation}) => {  // stands for props.navigation
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(0);
    const [blue, setBlue] = useState(0);

    return (
        <View>
            <ColorCounter
                onIncrease={() => setRed(red + 1)}
                onDecrease={() => setRed(red - 1)}
                color="Red"
            />
            <ColorCounter
                onIncrease={() => setBlue(blue + 1)}
                onDecrease={() => setBlue(blue - 1)}
                color="Blue"
            />
            <ColorCounter
                onIncrease={() => setGreen(green + 1)}
                onDecrease={() => setGreen(green - 1)}
                color="Green"
            />
            <View>
                style={{ height:200, width:200, backgroundColor: `rgb(${reg}, ${green}, ${blue})` }}
            </View>
        </View>
    );

};

const styles = StyleSheet.create({});

export default SquareScreen;
